const Handle = Slider.Handle;

class Sliders extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            rangeSliderValue: 0,
        }
    }
    handleSlider(evt) {
        const { value, dragging, index, ...restProps } = evt;

        return (
            <Tooltip
                prefixCls="rc-slider-tooltip"
                overlay={value}
                visible={dragging}
                placement="top"
                key={index}
            >
                <Handle value={value} {...restProps} />
            </Tooltip>
        );
    }

